import { Card, CardContent, Input, LinearProgress, Table, Button } from "@mui/joy";
import React, { useState, useEffect } from "react";
import axios from 'axios';
// import ControlCard from "./Components/ControlCard";
import _ from 'lodash';
// import Topbar from "./Components/Topbar";

export default function App() {
  const titleArray = ['Banking', 'Logistic', 'E-Commerce', 'Computer'];
  const [searchTerm, setSearchTerm] = useState("");
  const [users, setUsers] = useState([]);
  const [isReady, setIsReady] = useState(false);

  const handleDeleteUser = (userId) => {
 axios
      .delete("http://localhost:3001/api/user/" + userId)
      .then((res) => {
        getAllUser();
      })
      .catch((error) => {
        alert(error?.message);
        console.error("Error", error?.message);
      });
  };

  const getAllUser = () => {
    setIsReady(false)
    axios
      .get(`${process.env.REACT_APP_API_URL}/user `)
      .then((res) => {
        setUsers(res?.data?.rows);
        setIsReady(true);
        console.log("User ", res?.data?.rows);
      })
      .catch((error) => {
        console.error("Error", error?.message);
      });
  };

  useEffect(() => {
    getAllUser();
    return () => { };
  }, []);

  if (!isReady) {
    return (
      <div>
        <LinearProgress />
      </div>
    );
  }

  return (
    <div>
      <div className='min-h-screen'>
        <div className='flex justify-center '>
          <div className='lg:w-3/4'>
            <Card>
              <CardContent>
                <div>Search Box</div>
                <Input
                  placeholder='Input Some Search Word'
                  onChange={(e) => setSearchTerm(e.target.value)}
                />
                <div>
                  You Search <span className='text-blue-500'>{searchTerm}</span>
                </div>
              </CardContent>
            </Card>

            <h3 className='font-bold'>User List</h3>
            <Table>
              <thead>
                <tr>
                  <th>ลำดับที่</th>
                  <th>ชื่อ</th>
                  <th>แผนก</th>
                  <th>ดำเนินการ</th>
                </tr>
              </thead>
              {_.map(users, (eachUser, index) => (
                <tr>
                  <td>{index + 1}</td>
                  <td>{eachUser?.name}</td>
                  <td>{eachUser?.department}</td>
                  <td>
                    <Button color='danger' onClick={() => handleDeleteUser(eachUser?._id)}>ลบ</Button>
                  </td>
                </tr>
              ))}
            </Table>
          </div>
        </div>
      </div>

      {/* {titleArray.map(eachTitle => (
        <ControlCard title={eachTitle} />
      ))} */}

      <footer className="text-black">
        This is footer
      </footer>
    </div>

  );
}
