import React from "react";
import {Card, Input, CardContent, Controller, Button } from "@mui/joy";
export default function ControlCard({title}){
    const { control,handleSubmit } = useForm();
    const handleCreateUser = (data) => {
        console.log("data", data);
    };
    // const alertStart = ()=>{
    //     window.alert('คุณกำลังกด Start' +title);
    // };
    // const alertRestart = ()=>{
    //     window.alert('คุณกำลังกด Restart' +title);
    // };
    // const alertDown = ()=>{
    //     window.alert('คุณกำลังกด Down' +title);
    // };
  
    return(
        <div className='lg:w-3/4 '>
        <div className='my-1 font-semibold text-lg'>เพิ่มพนักงานใหม่</div>
        <Card>
          <CardContent>
          <form onSubmit={handleSubmit(handleCreateUser)}>
      <div>ชื่อ</div>
        <Controller
         name='name'
         control={control}
         render={({ field }) => (
             <Input {...field} placeholder='ชื่อพนักงาน' />
         )}/>
        <div>
            <Button type="submit">บันทึก</Button>
        </div>
   </form>
          </CardContent>
        </Card>
      </div>
    )
}
