import { Card, CardContent, Input, Table, Button } from "@mui/joy";
import React, { useState, useEffect } from "react";
import axios from 'axios';
// import { LinearProgress } from "@mui/material";
// import ControlCard from "./Components/ControlCard";
import _ from 'lodash';
// import Topbar from "./Components/Topbar";

export default function App() {
  const titleArray = ['Banking', 'Logistic', 'E-Commerce', 'Computer'];
  const [searchTerm, setSearchTerm] = useState("");
  const [users, setUsers] = useState([]);

  useEffect(() => {
    axios
    .get("http://localhost:3001/api/user")
      .then((res) => {
        setUsers(res?.data?.rows);
        console.log("User", res?.data?.rows);
      })
      .catch((error) => {
        console.error("Error", error?.message);
      });

    return () => { };
  }, []);

  return (
    <div>
      <div className='min-h-screen'>
        <div className='flex justify-center '>
          <div className='lg:w-3/4'>
            <Card>
              <CardContent>
                <div>Search Box</div>
                <Input
                  placeholder='Input Some Search Word'
                  onChange={(e) => setSearchTerm(e.target.value)}
                />
                <div>
                  You Search <span className='text-blue-500'>{searchTerm}</span>
                </div>
              </CardContent>
            </Card>

            <h3 className='font-bold'>User List</h3>
            <Table>
              <thead>
                <tr>
                  <th>ลำดับที่</th>
                  <th>ชื่อ</th>
                  <th>แผนก</th>
                  <th>ดำเนินการ</th>
                </tr>
              </thead>
              {_.map(users, (eachUser, index) => (
                  <tr>
                    <td>{index + 1}</td>
                    <td>{eachUser?.name}</td>
                    <td>{eachUser?.department}</td>
                    <td>
                      <Button color='danger'>ลบ</Button>
                    </td>
                  </tr>
                ))}
            </Table>
          </div>
        </div>
      </div>

      {/* {titleArray.map(eachTitle => (
        <ControlCard title={eachTitle} />
      ))} */}

      <footer className="text-black">
        This is footer
      </footer>
    </div>

  );
}
